package Java;

public class swapping2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


	 int a = 420, b =320;
	 
	 System.out.println("Before Swapping A = "+a+" And B= "+b);
	 
	 int swap = a;
	 a=b;
	 b=swap;
	 System.out.println("After Swapping A = "+a+" And B = "+b);
	}

}
